      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <div className="md:hidden absolute top-full left-0 right-0 bg-slate-900/95 backdrop-blur-2xl border-t border-white/10 p-8">
          <ul className="space-y-6">
            {[
              { label: 'Services', target: 'services' },
