import { useEffect } from 'react';

declare global {
  interface Window {
    gsap: any;
    ScrollTrigger: any;
    ScrollSmoother: any;
  }
}

export function useGSAPScrollSmoother() {
  useEffect(() => {
    if (typeof window === 'undefined' || !window.gsap) return;

    const { gsap } = window;

    // Check if plugins are available
    if (!window.ScrollTrigger || !window.ScrollSmoother) {
      console.warn('GSAP plugins not loaded, skipping ScrollSmoother initialization');
      return;
    }

    // Register GSAP plugins
    gsap.registerPlugin(window.ScrollTrigger, window.ScrollSmoother);

    // Create ScrollSmoother instance for liquid smooth scrolling
    const smoother = window.ScrollSmoother.create({
      wrapper: "#smooth-wrapper",
      content: "#smooth-content",
      smooth: 2,
      normalizeScroll: true,
      ignoreMobileResize: true,
      effects: true,
    });

    // Enhanced scroll-triggered animations that work with ScrollSmoother
    gsap.utils.toArray('.liquid-section').forEach((section: any, index: number) => {
      // Scale and fade animation with proper scroll-back behavior
      gsap.fromTo(section, 
        { 
          scale: 0.8,
          opacity: 0,
          rotationX: -15,
          filter: 'blur(10px)'
        },
        { 
          scale: 1,
          opacity: 1,
          rotationX: 0,
          filter: 'blur(0px)',
          duration: 1.5,
          ease: 'power2.out',
          scrollTrigger: {
            trigger: section,
            start: 'top 85%',
            end: 'bottom 15%',
            toggleActions: 'play none none reverse',
            refreshPriority: -1,
          }
        }
      );

      // Parallax effect for backgrounds
      if (section.querySelector('.bg-parallax')) {
        gsap.to(section.querySelector('.bg-parallax'), {
          yPercent: -50,
          ease: 'none',
          scrollTrigger: {
            trigger: section,
            start: 'top bottom',
            end: 'bottom top',
            scrub: true,
          }
        });
      }
    });

    // Liquid cards stagger animation with scroll-back
    gsap.utils.toArray('.liquid-card').forEach((card: any, index: number) => {
      gsap.fromTo(card,
        {
          y: 100,
          opacity: 0,
          scale: 0.8,
          rotationY: -45
        },
        {
          y: 0,
          opacity: 1,
          scale: 1,
          rotationY: 0,
          duration: 1,
          delay: index * 0.1,
          ease: 'back.out(1.7)',
          scrollTrigger: {
            trigger: card,
            start: 'top 90%',
            toggleActions: 'play none none reverse',
          }
        }
      );
    });

    // Text reveal animations with character-by-character effect
    gsap.utils.toArray('.liquid-text').forEach((text: any) => {
      const chars = text.textContent.split('');
      text.innerHTML = chars.map((char: string) => 
        char === ' ' ? '&nbsp;' : `<span class="char">${char}</span>`
      ).join('');

      gsap.fromTo(text.querySelectorAll('.char'),
        { 
          y: 100,
          opacity: 0,
          rotationX: -90
        },
        {
          y: 0,
          opacity: 1,
          rotationX: 0,
          duration: 0.6,
          stagger: 0.02,
          ease: 'back.out(2)',
          scrollTrigger: {
            trigger: text,
            start: 'top 80%',
            toggleActions: 'play none none reverse',
          }
        }
      );
    });

    // Navigation background animation
    const nav = document.querySelector('.liquid-nav');
    if (nav) {
      gsap.to(nav, {
        backgroundColor: 'rgba(15, 23, 42, 0.95)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(99, 102, 241, 0.2)',
        scrollTrigger: {
          trigger: 'body',
          start: 'top -100',
          end: 'bottom bottom',
          toggleActions: 'play none none reverse',
        }
      });
    }

    // Cleanup function
    return () => {
      if (window.ScrollTrigger) {
        window.ScrollTrigger.getAll().forEach((trigger: any) => trigger.kill());
      }
      if (smoother) {
        smoother.kill();
      }
    };
  }, []);
}

client/src/hooks/use-jeton-animations.ts.content:
import { useEffect } from 'react';

declare global {
  interface Window {
    gsap: any;
    ScrollTrigger: any;
  }
}

export function useJetonAnimations() {
  useEffect(() => {
    if (typeof window === 'undefined' || !window.gsap) return;

    const { gsap } = window;

    if (!window.ScrollTrigger) {
      console.warn('ScrollTrigger not loaded');
      return;
    }

    gsap.registerPlugin(window.ScrollTrigger);

    // Jeton-style flowing text animations
    const animateText = (selector: string, delay = 0) => {
      const elements = document.querySelectorAll(selector);
      elements.forEach((element, index) => {
        const text = element.textContent || '';
        const chars = text.split('');

        element.innerHTML = chars.map(char => 
          char === ' ' ? '&nbsp;' : `<span class="char" style="display: inline-block;">${char}</span>`
        ).join('');

        const charElements = element.querySelectorAll('.char');

        gsap.fromTo(charElements, 
          {
            y: 100,
            opacity: 0,
            rotation: 15,
            scale: 0.3
          },
          {
            y: 0,
            opacity: 1,
            rotation: 0,
            scale: 1,
            duration: 0.8,
            ease: "back.out(1.7)",
            stagger: 0.03,
            delay: delay + (index * 0.1),
            scrollTrigger: {
              trigger: element,
              start: "top 80%",
              toggleActions: "play none none reverse"
            }
          }
        );
      });
    };

    // Flowing card animations like Jeton
    const animateCards = () => {
      const cards = document.querySelectorAll('.flow-card');
      cards.forEach((card, index) => {
        gsap.fromTo(card,
          {
            y: 150,
            opacity: 0,
            scale: 0.8,
            rotationY: -25,
            z: -100
          },
          {
            y: 0,
            opacity: 1,
            scale: 1,
            rotationY: 0,
            z: 0,
            duration: 1.2,
            ease: "power3.out",
            delay: index * 0.15,
            scrollTrigger: {
              trigger: card,
              start: "top 85%",
              end: "bottom 20%",
              toggleActions: "play none none reverse"
            }
          }
        );

        // Hover effects
        card.addEventListener('mouseenter', () => {
          gsap.to(card, {
            scale: 1.05,
            rotationY: 5,
            z: 50,
            duration: 0.3,
            ease: "power2.out"
          });
        });

        card.addEventListener('mouseleave', () => {
          gsap.to(card, {
            scale: 1,
            rotationY: 0,
            z: 0,
            duration: 0.3,
            ease: "power2.out"
          });
        });
      });
    };

    // Flowing section transitions
    const animateSections = () => {
      const sections = document.querySelectorAll('.flow-section');
      sections.forEach((section, index) => {
        const direction = index % 2 === 0 ? -100 : 100;

        gsap.fromTo(section,
          {
            x: direction,
            opacity: 0,
            scale: 0.95,
            rotation: direction > 0 ? 2 : -2
          },
          {
            x: 0,
            opacity: 1,
            scale: 1,
            rotation: 0,
            duration: 1.5,
            ease: "power3.out",
            scrollTrigger: {
              trigger: section,
              start: "top 70%",
              end: "bottom 30%",
              toggleActions: "play none none reverse"
            }
          }
        );
      });
    };

    // Liquid hero animation
    const animateHero = () => {
      const hero = document.querySelector('.liquid-hero');
      if (!hero) return;

      gsap.fromTo('.hero-title', 
        {
          y: 100,
          opacity: 0,
          scale: 0.8
        },
        {
          y: 0,
          opacity: 1,
          scale: 1,
          duration: 1.5,
          ease: "power3.out",
          delay: 0.3
        }
      );

      gsap.fromTo('.hero-subtitle', 
        {
          y: 80,
          opacity: 0
        },
        {
          y: 0,
          opacity: 1,
          duration: 1.2,
          ease: "power3.out",
          delay: 0.6
        }
      );

      gsap.fromTo('.hero-cta', 
        {
          y: 60,
          opacity: 0,
          scale: 0.9
        },
        {
          y: 0,
          opacity: 1,
          scale: 1,
          duration: 1,
          ease: "back.out(1.7)",
          delay: 0.9
        }
      );
    };

    // Parallax background elements
    const animateParallax = () => {
      const parallaxElements = document.querySelectorAll('.parallax-bg');
      parallaxElements.forEach((element) => {
        gsap.to(element, {
          yPercent: -30,
          ease: "none",
          scrollTrigger: {
            trigger: element.parentElement,
            start: "top bottom",
            end: "bottom top",
            scrub: 1
          }
        });
      });
    };

    // Floating particles animation
    const animateParticles = () => {
      const particles = document.querySelectorAll('.floating-particle');
      particles.forEach((particle, index) => {
        gsap.to(particle, {
          y: "random(-100, 100)",
          x: "random(-50, 50)",
          rotation: "random(-180, 180)",
          duration: "random(8, 15)",
          ease: "none",
          repeat: -1,
          yoyo: true,
          delay: index * 0.5
        });
      });
    };

    // Initialize all animations
    setTimeout(() => {
      animateText('.flow-text');
      animateCards();
      animateSections();
      animateHero();
      animateParallax();
      animateParticles();
    }, 100);

    // Cleanup
    return () => {
      if (window.ScrollTrigger) {
        window.ScrollTrigger.getAll().forEach((trigger: any) => trigger.kill());
      }
    };
  }, []);
}

client/src/hooks/use-lenis.tsx.content:
import { useEffect, useRef } from 'react';

declare global {
  interface Window {
    Lenis: any;
  }
}

export function useLenis() {
  const lenisRef = useRef<any>(null);

  useEffect(() => {
    if (typeof window !== 'undefined' && window.Lenis) {
      lenisRef.current = new window.Lenis({
        duration: 1.2,
        easing: (t: number) => Math.min(1, 1.001 - Math.pow(2, -10 * t)),
        direction: 'vertical',
        gestureDirection: 'vertical',
        smooth: true,
        mouseMultiplier: 1,
        smoothTouch: false,
        touchMultiplier: 2,
        infinite: false,
      });

      const raf = (time: number) => {
        lenisRef.current?.raf(time);
        requestAnimationFrame(raf);
      };
      requestAnimationFrame(raf);

      return () => {
        lenisRef.current?.destroy();
      };
    }
  }, []);

  const scrollTo = (target: string | HTMLElement, options?: { offset?: number }) => {
    if (lenisRef.current) {
      lenisRef.current.scrollTo(target, options);
    }
  };

  return { scrollTo };
}

