interface Terminal {
  id: string;
  x: number;
  y: number;
  width: number;
  height: number;
  currentCommandIndex: number;
  currentOutputIndex: number;
  isTyping: boolean;
  typedText: string;
  lastUpdate: number;
}

export function CLITerminalBackground() {
  const containerRef = useRef<HTMLDivElement>(null);
  const [terminals, setTerminals] = useState<Terminal[]>([]);

  useEffect(() => {
    // Initialize terminals
    const initializeTerminals = () => {
      const newTerminals: Terminal[] = [
        {
          id: 'terminal-1',
          x: 5,
          y: 10,
          width: 500,
          height: 300,
          currentCommandIndex: 0,
          currentOutputIndex: 0,
          isTyping: false,
          typedText: '',
          lastUpdate: Date.now()
        },
        {
          id: 'terminal-2',
          x: 60,
          y: 40,
          width: 450,
          height: 250,
          currentCommandIndex: 1,
          currentOutputIndex: 0,
          isTyping: false,
          typedText: '',
          lastUpdate: Date.now()
        },
        {
          id: 'terminal-3',
          x: 25,
          y: 65,
          width: 480,
          height: 280,
          currentCommandIndex: 2,
          currentOutputIndex: 0,
          isTyping: false,
          typedText: '',
          lastUpdate: Date.now()
        }
      ];
      setTerminals(newTerminals);
    };

    initializeTerminals();

    // Typing animation
    const animateTyping = () => {
      setTerminals(prev => prev.map(terminal => {
        const now = Date.now();
        const currentCommand = securityCommands[terminal.currentCommandIndex];

        if (!terminal.isTyping && now - terminal.lastUpdate > 2000) {
          // Start typing new command
          return {
            ...terminal,
            isTyping: true,
            typedText: '',
            lastUpdate: now
          };
        }

        if (terminal.isTyping && now - terminal.lastUpdate > 50) {
          const targetText = `$ ${currentCommand.command}`;

          if (terminal.typedText.length < targetText.length) {
            // Continue typing command
            return {
              ...terminal,
              typedText: targetText.slice(0, terminal.typedText.length + 1),
              lastUpdate: now
            };
          } else {
            // Command finished, show output
            return {
              ...terminal,
              isTyping: false,
              currentCommandIndex: (terminal.currentCommandIndex + 1) % securityCommands.length,
              lastUpdate: now
            };
          }
        }

        return terminal;
      }));
    };

    const interval = setInterval(animateTyping, 50);
    return () => clearInterval(interval);
  }, []);

  return (
    <div ref={containerRef} className="fixed inset-0 pointer-events-none overflow-hidden">
